import './App.css';
import React, {useEffect, useRef} from "react";

let curNorthWestLat;
let curNorthWestLon;

let curSouthEastLat;
let curSouthEastLon;

let pixToCoord;

// global reference to the oldCanvas element
let canvas;

// global reference to the oldCanvas' context
let ctx;

function Canvas(props) {
    let w = 800;
    let h = 500;
    let ref = useRef();
    useEffect(() => {
        canvas = ref.current;
        pixToCoord = 200;

        curSouthEastLat = 41.823876;
        curSouthEastLon = -71.395963;

        curNorthWestLat = (curSouthEastLat - w/pixToCoord);
        curNorthWestLon = (curSouthEastLon - h/pixToCoord);

        ctx = canvas.getContext("2d");
        ctx.lineWidth = 1;

        let rawWaysData = props.routeToRender
        let listOfWays = [];
        if (rawWaysData != null) {
            listOfWays = (rawWaysData + '').split(";")
        } else {
            ctx.beginPath();
            ctx.moveTo(Math.random()*20+50, 50);
            ctx.lineTo(90, 90);
            ctx.strokeStyle = "blue";
            ctx.stroke();
        }

        let i;
        let parsedWay;
        let type;
        let startLat;
        let startLon;
        let EndLat;
        let EndLon;
        for(i=0; i<listOfWays.length; i++){
            parsedWay =  listOfWays[i].split(",");
            type = parsedWay[0]
            startLat = parseFloat(parsedWay[1]);
            startLon = parseFloat(parsedWay[2]);
            EndLat = parseFloat(parsedWay[3]);
            EndLon = parseFloat(parsedWay[4]);

            ctx.beginPath();
            ctx.moveTo((startLat - curNorthWestLat)*pixToCoord, (startLon - curNorthWestLon)*pixToCoord);
            ctx.lineTo((EndLat - curNorthWestLat)*pixToCoord, (EndLon - curNorthWestLon)*pixToCoord);
            ctx.strokeStyle = "red";
            ctx.stroke();


        }
        //ctx.beginPath();
        //ctx.moveTo(0, 0);
        //ctx.lineTo(100, 100);
        //ctx.strokeStyle = "red";
        //ctx.stroke();

    })
    return (
        <div className="Canvas">
            <canvas
                ref={ref}
                style={{ width: w, height: h }}
                routeToRender={props.routeToRender}
                onChange={(e) => props.onChange(e.target.value)}
            />

        </div>
    );
}

export default Canvas;
